import React from "react";
import styles from "./Experience.module.css";
import miFoto from "../../assets/hero/miFoto.png";
import skills from "../../data/skills.json";



// Componente de experiencia
export const Experience = () => {
    return (
      <section className={styles.container} id="experience">
        {/* Título de la sección */}
        <h2 className={styles.title}>Experience</h2>
        <div className={styles.content}>
          
          {/* Sección de habilidades */}
          <div className={styles.skills}>
            {skills.map((skill, id) => { // Itera sobre las habilidades
              return (
                <div key={id} className={styles.skill}>
                  <div className={styles.skillImageContainer}>
                    {/* Imagen de habilidad */}
                    <img src={getImageUrl(skill.imageSrc)} alt={skill.title} />
                  </div>
                  {/* Título de la habilidad */}
                  <p>{skill.title}</p>
                </div>
              );
            })}
          </div>
  
          {/* Sección de historial laboral */}
          <ul className={styles.history}>
            {history.map((historyItem, id) => { // Itera sobre el historial laboral
              return (
                <li key={id} className={styles.historyItem}>
                  {/* Logo de la empresa */}
                  <img
                    src={getImageUrl(historyItem.imageSrc)}
                    alt={`${historyItem.organisation} Logo`}
                  />
                  <div className={styles.historyItemDetails}>
                    {/* Detalles de la experiencia laboral */}
                    <h3>{`${historyItem.role}, ${historyItem.organisation}`}</h3>
                    <p>{`${historyItem.startDate} - ${historyItem.endDate}`}</p>
                    <ul>
                      {historyItem.experiences.map((experience, id) => { // Itera sobre las experiencias laborales
                        return <li key={id}>{experience}</li>;
                      })}
                    </ul>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </section>
    );
  };
