import React from "react";
import styles from "./Experience.module.css";

// Importaciones de im치genes de skills
import htmlIcon from "../../assets/skills/html.png";
import cssIcon from "../../assets/skills/css.png";
import reactIcon from "../../assets/skills/react.png";
import nodeIcon from "../../assets/skills/node.png";
import graphqlIcon from "../../assets/skills/graphql.png";
import mongoIcon from "../../assets/skills/mongodb.png";
import figmaIcon from "../../assets/skills/figma.png";

// Importaciones de im치genes de historia
import googleLogo from "../../assets/history/google.png";
import microsoftLogo from "../../assets/history/microsoft.png";
import netflixLogo from "../../assets/history/netflix.png";

// Crear un objeto que mapee los nombres de archivo a las importaciones
const skillImages = {
  "html.png": htmlIcon,
  "css.png": cssIcon,
  "react.png": reactIcon,
  "node.png": nodeIcon,
  "graphql.png": graphqlIcon,
  "mongodb.png": mongoIcon,
  "figma.png": figmaIcon
};

const historyImages = {
  "google.png": googleLogo,
  "microsoft.png": microsoftLogo,
  "netflix.png": netflixLogo
};

export const Experience = () => {
    return (
      <section className={styles.container} id="experience">
        <h2 className={styles.title}>Experience</h2>
        <div className={styles.content}>
          
          <div className={styles.skills}>
            {skills.map((skill, id) => {
              return (
                <div key={id} className={styles.skill}>
                  <div className={styles.skillImageContainer}>
                    <img 
                      src={skillImages[skill.imageSrc]} 
                      alt={skill.title} 
                    />
                  </div>
                  <p>{skill.title}</p>
                </div>
              );
            })}
          </div>

          <ul className={styles.history}>
            {history.map((historyItem, id) => {
              return (
                <li key={id} className={styles.historyItem}>
                  <img
                    src={historyImages[historyItem.imageSrc]}
                    alt={`${historyItem.organisation} Logo`}
                  />
                  <div className={styles.historyItemDetails}>
                    <h3>{`${historyItem.role}, ${historyItem.organisation}`}</h3>
                    <p>{`${historyItem.startDate} - ${historyItem.endDate}`}</p>
                    <ul>
                      {historyItem.experiences.map((experience, id) => (
                        <li key={id}>{experience}</li>
                      ))}
                    </ul>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </section>
    );
};
