import React from "react";
import styles from "./Experience.module.css";
import { getImageUrl } from "../../utils"; 
import skills from "../../data/skills.json";
import history from "../../data/history.json"; 
import foto from "../../assets/skills/";

// Definimos las rutas base para las imágenes
const SKILLS_IMAGE_PATH = "../../assets/skills";
const HISTORY_IMAGE_PATH = "../../assets/history/";

export const Experience = () => {
    return (
      <section className={styles.contenedor} id="experience">
        <h2 className={styles.titulo}>Experience</h2>
        <div className={styles.contenido}>
          <div className={styles.skills}>
            {skills.map((skill, id) => {
              return (
                <div key={id} className={styles.skill}>
                  <div className={styles.skillImageContainer}>
                    <img 
                      src={`${SKILLS_IMAGE_PATH}${skill.imageSrc}`}
                      alt={skill.title} 
                    />
                  </div>
                  <p>{skill.title}</p>
                </div>
              );
            })}
          </div>
          <ul className={styles.history}>
            {history.map((historyItem, id) => {
              return (
                <li key={id} className={styles.historyItem}>
                  <img
                    src={`${HISTORY_IMAGE_PATH}${historyItem.imageSrc}`}
                    alt={`${historyItem.organisation} Logo`}
                  />
                  <div className={styles.historyItemDetails}>
                    <h3>{`${historyItem.role}, ${historyItem.organisation}`}</h3>
                    <p>{`${historyItem.startDate} - ${historyItem.endDate}`}</p>
                    <ul>
                      {historyItem.experiences.map((experience, id) => {
                        return <li key={id}>{experience}</li>;
                      })}
                    </ul>
                  </div>
                </li>
              );
            })}
          </ul>
        </div>
      </section>
    );
  };
